import React from "react";
import "./styles/header.css";
import RouteContext from "../Context";
import { useContext } from "react";

const Header = () => {
  const { page, setPage } = useContext(RouteContext);
  return (
    <div className="header">
      <ul>
        <li
          onClick={() => setPage(0)}
          style={{ color: page === 0 ? "rgb(169, 178, 72)" : "#bababa" }}
        >
          Home
        </li>
        <li onClick={() => setPage(1)}>Beekeeping</li>
        <li onClick={() => setPage(2)}>Store</li>
        <li onClick={() => setPage(3)}>Blog</li>
        <li onClick={() => setPage(4)}>Bits 'n' pieces</li>
        <li onClick={() => setPage(5)}>About</li>
      </ul>
    </div>
  );
};

export default Header;
