import React from "react";
import "./styles/header.css";
import RouteContext from "../Context";
import { useContext } from "react";

const Header = () => {
  const { page, setPage } = useContext(RouteContext);
  return (
    <div className="header">
      <ul>
        <li
          onClick={() => setPage(0)}
          style={{
            color: page === 0 ? "rgb(169, 178, 72)" : "#bababa",
            transition: "color 0.3s",
            ":hover": {
              color: "blue",
            },
          }}
        >
          Home
        </li>
        <li
          onClick={() => setPage(1)}
          style={{ color: page === 1 ? "rgb(169, 178, 72)" : "#bababa" }}
        >
          Beekeeping
        </li>
        <li
          onClick={() => setPage(2)}
          style={{ color: page === 2 ? "rgb(169, 178, 72)" : "#bababa" }}
        >
          Store
        </li>
        <li
          onClick={() => setPage(3)}
          style={{ color: page === 3 ? "rgb(169, 178, 72)" : "#bababa" }}
        >
          Blog
        </li>
        <li
          onClick={() => setPage(4)}
          style={{ color: page === 4 ? "rgb(169, 178, 72)" : "#bababa" }}
        >
          Bits 'n' pieces
        </li>
        <li
          onClick={() => setPage(5)}
          style={{ color: page === 5 ? "rgb(169, 178, 72)" : "#bababa" }}
        >
          About
        </li>
      </ul>
    </div>
  );
};

export default Header;
